document.addEventListener("DOMContentLoaded", function() {
    // Gráfico de Cumplimiento
    var ctx = document.getElementById('complianceChart').getContext('2d');
    var complianceChart = new Chart(ctx, {
        type: 'doughnut',
        data: {
            labels: ['Cumplidos', 'Pendientes'],
            datasets: [{
                data: [10, 4],
                backgroundColor: ['#4caf50', '#f44336']
            }]
        },
        options: {
            responsive: true,
            plugins: {
                legend: {
                    position: 'top',
                }
            }
        }
    });

    // Gráfico de Evaluación de Riesgos
    var ctx2 = document.getElementById('riskChart').getContext('2d');
    var riskChart = new Chart(ctx2, {
        type: 'bar',
        data: {
            labels: ['Identificados', 'Mitigados', 'Pendientes'],
            datasets: [{
                label: 'Riesgos',
                data: [8, 5, 3],
                backgroundColor: ['#ff9800', '#4caf50', '#f44336']
            }]
        },
        options: {
            responsive: true,
            plugins: {
                legend: {
                    position: 'top',
                }
            }
        }
    });
});
